matproxy.Add({	name = "BurnLevel",	init = function(self, mat, kv)		self.Result = kv.resultvar	end,		bind = function(self, mat, ent)		if (!IsValid(ent)) then return end		local burnlevel = ent:GetProxyVar("BurnLevel")		if (!ent:IsPlayer()) then			burnlevel = ent:GetNWInt("BurnLevel")		end		if IsValid(ent) and burnlevel then			mat:SetFloat("$detailblendfactor", burnlevel) 		elseif IsValid(ent) then			mat:SetFloat("$detailblendfactor", 0)		end	end})